script:
frappe.ui.form.on('Meeting', {
    onload: function(frm) {
        frm.fields_dict['attendees'].grid.get_field('attendee_name').get_query = function(doc, cdt, cdn) {
            return {
                filters: {
                    status: "Active"
                }
            };
        };
    }
});

frappe.ui.form.on('BOQ', {
    onload: function(frm) {
        frm.fields_dict['items'].grid.get_field('item_code').get_query = function(doc, cdt, cdn) {
            let child = locals[cdt][cdn];
            return {
                query: "erpnext.controllers.queries.item_query",
                filters: {
                    'price_list': doc.selling_price_list
                }
            };
        };
    }
});

frappe.ui.form.on('Account', {
    account_type(frm) {
        if (frm.doc.account_type == "Asset") {
            frm.set_value("custom_naming_series", "AST-.####");
        } else if (frm.doc.account_type == "Liability") {
            frm.set_value("custom_naming_series", "LIA-.####");
        } else if (frm.doc.account_type == "Income Account") {
            frm.set_value("custom_naming_series", "INC-.####");
        }
    }
});

frappe.ui.form.on('Sales Order', {
    po_no: function (frm) {
        if (!frm.doc.po_no || !frm.doc.customer) return;

        frappe.call({
            method: 'frappe.client.get_list',
            args: {
                doctype: 'Sales Order',
                filters: {
                    po_no: frm.doc.po_no,
                    customer: frm.doc.customer,
                    docstatus: ['<', 2],
                    name: ['!=', frm.doc.name]
                },
                fields: ['name']
            },
            callback: function (r) {
                if (r.message && r.message.length > 0) {
                    frappe.msgprint({
                        title: __('Warning'),
                        message: __('This Purchase Order Number is already used in another Sales Order.'),
                        indicator: 'orange'
                    });
                }
            }
        });
    }
});

frappe.ui.form.on('Item Request', {
    item_code: {
        get_query: function(frm, cdt, cdn) {
            return {
                filters: {
                    party_type: frm.doc.party_type
                }
            };
        }
    }
});

frappe.ui.form.on('Sales Invoice', {
    selling_price_list: function(frm) {
        if (!frm.doc.selling_price_list) return;

        frappe.call({
            method: 'frappe.client.get_list',
            args: {
                doctype: 'Item Price',
                filters: {
                    price_list: frm.doc.selling_price_list
                },
                fields: ['item_code']
            },
            callback: function(r) {
                if (r.message) {
                    let allowed_items = r.message.map(d => d.item_code);
                    
                    // نضع التصفية على كل صف من صفوف items
                    frm.fields_dict.items.grid.get_field('item_code').get_query = function(doc, cdt, cdn) {
                        return {
                            filters: {
                                item_code: ['in', allowed_items]
                            }
                        };
                    };
                }
            }
        });
    }
});

frappe.ui.form.on('Meeting', {
  before_save: function(frm) {
    let email_list = [];
    if (frm.doc.attendees && frm.doc.attendees.length > 0) {
      frm.doc.attendees.forEach(function(attendee) {
        if (attendee.email) {
          email_list.push(attendee.email);
        }
      });

      if (email_list.length > 0) {
        frm.set_value('attendee_emails', email_list.join(", "));
        console.log("Emails collected:", email_list.join(", "));
      } else {
        console.log("No emails found.");
        frappe.msgprint(__('لم يتم العثور على أي إيميلات.'));
      }
    }
  },
  on_submit: function(frm) {
    if (frm.doc.attendee_emails) {
      let attendee_names = frm.doc.attendees.map(attendee => {
        return attendee.employee_name || attendee.attendee_name;
      }).join("<br>");

      frappe.db.get_value('Employee', frm.doc.meeting_manager, 'employee_name').then(r => {
        let manager_name = r && r.message ? r.message.employee_name : frm.doc.meeting_manager;

        frappe.call({
          method: "frappe.core.doctype.communication.email.make",
          args: {
            recipients: frm.doc.attendee_emails,
            subject: `📅 دعوة اجتماع بتاريخ: ${frm.doc.meeting_date || "غير محدد"}`,
            content: `
              <div dir="rtl" style="text-align: right; font-family: 'Tahoma', sans-serif; border: 1px solid #ddd; padding: 20px;">
                <h2 style="color: #007bff;">دعوة لحضور اجتماع</h2>
                <p style="font-family: 'Tahoma';">مرحبًا،</p>
                <p style="font-family: 'Tahoma';">يوجد لديكم موعد اجتماع:</p>
                <table style="width: 100%; border-collapse: collapse; margin-bottom: 20px; font-family: 'Tahoma';">
                  <tr>
                    <td style="width: 20%;"><strong>التاريخ:</strong></td>
                    <td>${frm.doc.meeting_date || "dd/mm/yyyy"}</td>
                  </tr>
                  <tr>
                    <td><strong>الوقت:</strong></td>
                    <td>${frm.doc.meeting_time || "hh:mm"}</td>
                  </tr>
                  <tr>
                    <td><strong>الموقع:</strong></td>
                    <td>${frm.doc.location || "غير محدد"}</td>
                  </tr>
                  <tr>
                    <td><strong>الموضوع:</strong></td>
                    <td>${frm.doc.meeting_subject || "غير محدد"}</td>
                  </tr>
                  <tr>
                    <td><strong>الحضور:</strong></td>
                    <td>${attendee_names}</td>
                  </tr>
                  <tr>
                    <td><strong>اسم مدير الاجتماع:</strong></td>
                    <td>${manager_name}</td>
                  </tr>
                </table>
                <p style="font-family: 'Tahoma';">يرجى التأكيد على الحضور أو التواصل في حال وجود أي استفسارات.</p>
                <p style="font-family: 'Tahoma';"><strong>شكرًا لكم،</strong></p>
              </div>
            `,
            communication_medium: "Email",
            send_email: 1
          },
          callback: function(response) {
            frappe.msgprint(__('تم إرسال دعوة الاجتماع بنجاح.'));
          }
        });
      });
    } else {
      frappe.msgprint(__('لا توجد إيميلات لإرسال الدعوة. تأكد من إضافة إيميلات صحيحة.'));
    }
  }
});

frappe.ui.form.on('Meeting Minutes', {
  before_save: function(frm) {
    if (frm.doc.meeting_id) {  // تأكد من أن meeting_id موجود
      frappe.db.get_doc('Meeting', frm.doc.meeting_id).then(meeting => {
        if (meeting.attendees && meeting.attendees.length > 0) {
          let email_list = [];
          meeting.attendees.forEach(attendee => {
            if (attendee.email) {
              email_list.push(attendee.email);
            }
          });

          if (email_list.length > 0) {
            frm.set_value('attendee_emails', email_list.join(", "));
          }
        }
      });
    }
  }
});
